(()=>{window.addEventListener("DOMContentLoaded",function(){let n=document.getElementById("translation_menu"),t=document.getElementById("translation_btn"),e=n.parentNode;t.addEventListener("touchend",function(){n.classList.contains("hidden")?n.classList.remove("hidden"):n.classList.add("hidden")}),e.addEventListener("mouseenter",function(){n.classList.remove("hidden")}),e.addEventListener("mouseleave",function(){n.classList.add("hidden")})});})();
